export const rightToc = [{
  value: 'Docker Editions',
  id: 'docker-editions',
  children: []
}, {
  value: 'General',
  id: 'general',
  children: []
}, {
  value: 'Intro',
  id: 'intro',
  children: []
}, {
  value: 'General',
